import React from "react";
import ReactDOM from "react-dom";
import FileBase64 from 'react-file-base64';

export default class App extends React.Component {
	constructor(props){
		super(props);

		this.state = { 
			identifedAs: '',
			files: ''
		};
	}

	getFiles(files){
		console.log(files)
		console.log(files[0].base64)
	    this.setState({ files: files })
	    this.identifyImage(this.state.files[0].base64);
	  }

	identifyImage (picture) {
		const Clarifai = require('clarifai');

		const clarifaiApp = new Clarifai.App({
			apiKey: 'ba7fec128a0243c18186d47d861edb35'
		});

		clarifaiApp.models.predict(Clarifai.GENERAL_MODEL, {base64: picture})
			.then((response) => this.displayAnswer(response.outputs[0].data.concepts[0].name)
			.catch((err) => alert(err))
		);
	  }

	render () {
		//this.identifyImage("https://jareddubina.com/images/portfolio/jrd.png");

		return (
			<div>
	            <FileBase64
			        multiple={ true }
			        onDone={ this.getFiles.bind(this) } />
			</div>
		)
	}

}


ReactDOM.render(<App />, document.getElementById("image-sizer-app"));
