import React from "react";
import ReactDOM from "react-dom";

export default class App extends React.Component {
	constructor(props){
		super(props);

		this.state = { 
			identifedAs: ''
		}
	}

	identifyImage (image) {
		const Clarifai = require('clarifai');

		const clarifaiApp = new Clarifai.App({
			apiKey: 'ba7fec128a0243c18186d47d861edb35'
		});

		clarifaiApp.models.initModel({id: Clarifai.GENERAL_MODEL, version: "aa7f35c01e0642fda5cf400f543e7c40"})
	      .then(generalModel => {
	        return generalModel.predict(image);
	      })
	      .then(response => {
	        let concepts = response['outputs'][0]['data']['concepts']
	        console.log(concepts)

	        for (let i=0; i < concepts.length; i++) {
	        	if (concepts[i].name === "looking") {
	        		console.log('true => ' + concepts[i].name)
	      			return
	        	} else {
	        		console.log('false => ' + concepts[i].name)
	        	}
	        }
	      })
	  }

	  componentWillMount () {
	  	const image = 'https://jareddubina.com/images/portfolio/jrd.png';
	  	const twoDImage = image.getContext('2d');
	  	twoDImage.getImageData(20,20,20,20);

	  	const dataURL = image.toDataURL("image/png");
	  	return dataURL.replace(/^data:image\/(png|jpg);base64,/, "");
	  }

	render () {
		this.identifyImage("https://jareddubina.com/images/portfolio/jrd.png");

		return (
			<div>
				<h1>Hello World</h1>
			</div>
		)
	}

}


ReactDOM.render(<App />, document.getElementById("image-sizer-app"));
