import React from "react";
import ReactDOM from "react-dom";
import ImageUploader from 'react-images-upload';
import FileBase64 from 'react-file-base64';

export default class App extends React.Component {
	constructor(props){
		super(props);

		this.state = { 
			identifedAs: '',
			pictures: [],
			files: []
		};
		this.onDrop = this.onDrop.bind(this);
	}

	getFiles(files){
	    this.setState({ files: files })
	  }

	onDrop(picture) {
        this.setState({
            pictures: this.state.pictures.concat(picture),
        });
        this.identifyImage(picture);
    }

	identifyImage (picture) {
		const Clarifai = require('clarifai');

		const clarifaiApp = new Clarifai.App({
			apiKey: 'ba7fec128a0243c18186d47d861edb35'
		});

		clarifaiApp.models.initModel({id: Clarifai.GENERAL_MODEL, version: "aa7f35c01e0642fda5cf400f543e7c40"})
	      .then(generalModel => {
	        return generalModel.predict(picture);
	      })
	      .then(response => {
	        let concepts = response['outputs'][0]['data']['concepts']
	        console.log(concepts)

	        for (let i=0; i < concepts.length; i++) {
	        	if (concepts[i].name === "looking") {
	        		console.log('true => ' + concepts[i].name)
	      			return
	        	} else {
	        		console.log('false => ' + concepts[i].name)
	        	}
	        }
	      })
	  }

	render () {
		this.identifyImage("https://jareddubina.com/images/portfolio/jrd.png");

		return (
			<div>
	            <FileBase64
			        multiple={ true }
			        onDone={ this.getFiles.bind(this) } />
			</div>
		)
	}

}


ReactDOM.render(<App />, document.getElementById("image-sizer-app"));
